    (function initVetet(){
        let isViewModalVetet = localStorage.getItem("vetetModalViewed")
        let isShowVetetIntroRight = localStorage.getItem("showVetetIntroRight")
        if(!isShowVetetIntroRight) {
            isShowVetetIntroRight = 'show'
        }

        if(!isViewModalVetet && window.screen.width <= 1200) {
            $('#modal--vetetIntro').modal('show');
            localStorage.setItem("vetetModalViewed", true);
        } else {
            $('#closeVetetIntro').click(function() {
                $('#vetetIntro').toggle('slide')
                $('#showVetetIntro').toggle('slide')
                localStorage.setItem("showVetetIntroRight", 'hide')
            })

            $('#showVetetIntro').click(function(){
                $('#vetetIntro').toggle('slide')
                $('#showVetetIntro').toggle('slide')
                localStorage.setItem("showVetetIntroRight", 'show')
            })

            if(isShowVetetIntroRight == 'show') {
                $('#vetetIntro').show()
                $('#showVetetIntro').hide()
            } else {
                $('#vetetIntro').hide()
                $('#showVetetIntro').show()
            }
            
        }
    })()

